package nankisu.study.springbatch.jobrepogitory.config;

import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.HashMap;
import java.util.Map;

import javax.sql.DataSource;

import org.springframework.batch.core.configuration.annotation.SimpleBatchConfiguration;
import org.springframework.batch.core.repository.ExecutionContextSerializer;
import org.springframework.batch.core.repository.JobRepository;
import org.springframework.batch.core.repository.support.JobRepositoryFactoryBean;
import org.springframework.boot.autoconfigure.batch.BasicBatchConfigurer;
import org.springframework.boot.autoconfigure.batch.BatchProperties;
import org.springframework.boot.autoconfigure.transaction.TransactionManagerCustomizers;
import org.springframework.context.annotation.Configuration;

@Configuration
public class MyBatchConfig extends BasicBatchConfigurer{
	private final DataSource dataSource;
	
	protected MyBatchConfig(BatchProperties properties, DataSource dataSource,
			TransactionManagerCustomizers transactionManagerCustomizers) {
		super(properties, dataSource, transactionManagerCustomizers);
		this.dataSource = dataSource;
	}
	
	@Override
	protected JobRepository createJobRepository() throws Exception {
		JobRepositoryFactoryBean jobRepositoryFactoryBean = new JobRepositoryFactoryBean();
		jobRepositoryFactoryBean.setDataSource(dataSource);
		jobRepositoryFactoryBean.setTransactionManager(getTransactionManager());
		jobRepositoryFactoryBean.setSerializer(new MySerializer());
		return jobRepositoryFactoryBean.getObject();
	}
	
	public class MySerializer implements ExecutionContextSerializer {
		@Override
		public void serialize(Map<String, Object> object, OutputStream outputStream) throws IOException {
			// TODO Auto-generated method stub
			outputStream.write("".toString().getBytes());
		}

		@Override
		public Map<String, Object> deserialize(InputStream inputStream) throws IOException {
			// TODO Auto-generated method stub
			return new HashMap<String, Object>();
		}
	}
}
